import React, { useState, useEffect } from "react";
import axios from "axios";
import SearchBar from "../components/Searchbar";
import MovieCard from "../components/MovieCard";
import "./Home.css"; // Optional: Add custom styles for the Home page

function Home() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch random movies on component mount
  useEffect(() => {
    const fetchRandomMovies = async () => {
      try {
        const randomTerms = ["action", "comedy", "drama", "horror", "sci-fi"];
        const randomTerm = randomTerms[Math.floor(Math.random() * randomTerms.length)];
        const response = await axios.get(
          `https://www.omdbapi.com/?s=${randomTerm}&apikey=${process.env.REACT_APP_OMDB_API_KEY}`
        );
        setMovies(response.data.Search || []);
      } catch (error) {
        console.error("Error fetching random movies:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchRandomMovies();
  }, []);

  // Handle search functionality
  const handleSearch = async (query) => {
    setLoading(true);
    try {
      const response = await axios.get(
        `https://www.omdbapi.com/?s=${query}&apikey=${process.env.REACT_APP_OMDB_API_KEY}`
      );
      setMovies(response.data.Search || []);
    } catch (error) {
      console.error("Error searching movies:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home">
      <h1 style={{ textAlign: "center", marginTop: "20px" }}>Movie Explorer</h1>
      <SearchBar onSearch={handleSearch} />
      {loading ? (
        <p style={{ textAlign: "center" }}>Loading...</p>
      ) : (
        <div className="movie-list">
          {movies.map((movie) => (
            <MovieCard key={movie.imdbID} movie={movie} />
          ))}
        </div>
      )}
    </div>
  );
}

export default Home;
